import Vector from '../vector';
import { settings } from '../globals';

/**
 * The doors that separate one map to the next.
 */
class Door {

  constructor(pos) {
    this.pos = pos.plus(new Vector(0.1, 0.1));
    this.size = new Vector(5.2, 10.3);
    this.type = 'door';
    this.actorType = 'item';
    this.coords = {
      left: this.pos.x,
      right: this.pos.x + this.size.x,
      top: this.pos.y,
      bottom: this.pos.y + this.size.y,
    };
  }

  static act(step) { /* Leave this blank. */ }

}

export default Door;

import Vector from '../vector';
import { settings } from '../globals';

/**
 * Invisible markers that represent a player passing a particular level.
 */
class Flag {

  constructor(pos) {
    this.pos = pos.plus(new Vector(0.1, 0.1));
    this.size = new Vector(2, 2);
    this.wobble = 2 * Math.PI * Math.random();
    this.type = 'flag';
    this.actorType = 'item';
    this.coords = {
      left: this.pos.x,
      right: this.pos.x + this.size.x,
      top: this.pos.y,
      bottom: this.pos.y + this.size.y
    };
  }

  act(step) {
    this.wobble += step * settings.wobbleSpeed;
    let wobblePos = Math.sin(this.wobble) * settings.wobbleDist;
    this.pos = this.pos.plus(new Vector(0, wobblePos));
  }

}

export default Flag

